"use strict";

$(document).ready(function () {
  var t = $("body"),
      a = $(".form__input-date");
  var e = $(".btn-SignIn");
  var o = $(".modal-wrapper1"),
      l = $(".modal-wrapper2"),
      c = $(".modal__title_CheckIn"),
      i = $(".modal__title_signIn"),
      n = $(".form__btn_cancel"),
      s = $(".modal");

  function r() {
    o.removeClass("modal-wrapper1--active"), i.removeClass("modal__title_active");
  }

  function d() {
    o.toggleClass("modal-wrapper1--active"), i.addClass("modal__title_active"), m();
  }

  function m() {
    l.removeClass("modal-wrapper2--active"), c.removeClass("modal__title_active");
  }

  a.focus(function () {
    this.setAttribute("type", "date");
  }), a.blur(function () {
    this.setAttribute("type", "text");
  }), e.click(function (a) {
    a.stopPropagation(), t.toggleClass("no-scroll"), l.hasClass("modal-wrapper2--active") ? m() : d();
  }), s.click(function (t) {
    t.stopPropagation();
  }), c.click(function () {
    l.addClass("modal-wrapper2--active"), c.addClass("modal__title_active"), r();
  }), i.click(function () {
    m(), d();
  }), n.click(function () {
    m();
  }), $(document).click(function (a) {
    a.target !== s && (r(), m(), t.removeClass("no-scroll"));
  });
});